mapper.insert:
insert into TB_COMM_MEMBER_TEMP (
              MEM_UID
            , MEM_NAME
            , MEM_PHONE
            , MEM_REG_NUM
            , MEM_CHK
            , MEM_RDO
            , MEM_DESC
            , CREATE_DT
) values (
            SYS_GUID()
            , #{memName}
            , #{memPhone}
            , #{memRegNum}
            , #{memChk}
            , #{memRdo}
            , #{memDesc}
            , SYSDATE + 9/24
)

mapper.select:
SELECT TEMP.MEM_UID , SUBSTR(TEMP.MEM_NAME, 1,1) || '*' || SUBSTR(TEMP.MEM_NAME, 3,1) AS MEM_NAME , SUBSTR(TEMP.MEM_PHONE, 1, 3) || '-' || '****' || '-' || SUBSTR(TEMP.MEM_PHONE, 8, 4) AS MEM_PHONE , RPAD(SUBSTR(TEMP.MEM_REG_NUM, 1, 6) || '-' || SUBSTR(TEMP.MEM_REG_NUM, 7, 1), 14, '*') AS MEM_REG_NUM , REPLACE(REPLACE(REPLACE(REPLACE(TEMP.MEM_CHK, 'C', 'CRUD'), 'Q', 'Query'), 'F', 'Function'), 'P', 'Procedure') AS MEM_CHK , CASE WHEN TEMP.MEM_RDO = 'J' THEN '일본' WHEN TEMP.MEM_RDO = 'A' THEN '동남아' WHEN TEMP.MEM_RDO = 'E' THEN '유럽' ELSE TEMP.MEM_RDO END AS MEM_RDO , TEMP.MEM_DESC , TEMP.CREATE_DT , MEM.MEM_UID , MEM.CREATE_DT AS CONF_DATE FROM TB_COMM_MEMBER_TEMP TEMP LEFT OUTER JOIN TB_COMM_MEMBER MEM ON MEM.MEM_UID = TEMP.MEM_UID
AND TEMP.MEM_NAME LIKE '%' || #{memName} || '%'
AND TEMP.MEM_REG_NUM LIKE '%' || #{memRegNum} || '%'
AND TEMP.MEM_PHONE LIKE #{memPhone} || '%'
AND TEMP.MEM_RDO = #{memRdo}
AND TEMP.MEM_CHK = #{memChk}
