SET comm_dtl_nm  = #{commDtlNm}
  , comm_dtl_desc  = #{commDtlDesc}
  , use_yn  = #{useYn}
  , UPDATE_USER_ID  = #{updateUserId}
  , UPDATE_DT  = #{updateDt}
WHERE comm_dtl_cd = #{commDtlCd}
  and comm_cd = #{commCd}

mapper.delete:
DELETE
FROM TB_COMM_DTL_CD
WHERE comm_dtl_cd = #{commDtlCd}
  and comm_cd = #{commCd}

mapper.select:
SELECT COMM_CD , COMM_NM , COMM_DESC , CREATE_USER_ID , CREATE_DT , UPDATE_USER_ID , UPDATE_DT FROM TB_COMM_CD
AND COMM_CD = #{commCd}
AND COMM_NM = #{commNm}

mapper.insert:
INSERT INTO TB_COMM_CD (
         COMM_CD
       , COMM_NM
       , COMM_DESC
       , CREATE_USER_ID
       , CREATE_DT
) VALUES (
         #{commCd}
       , #{commNm}
       , #{commDesc}
       , #{createUserId}
       , #{createDt}
)

