mapper.select:
select comm_cd, comm_nm, comm_desc, create_user_id, create_dt
from TB_COMM_CD


        AND COMM_CD = #{commCd}


        AND COMM_NM = #{commNm}


        AND comm_desc = #{commDesc}


        AND create_user_id = #{createUserId}


        AND create_dt = #{createDt}

select comm_cd, comm_nm, comm_desc, create_user_id, create_dt, UPDATE_USER_ID, UPDATE_DT
from TB_COMM_CD
where COMM_CD = #{commCd}

mapper.insert:
INSERT INTO TB_COMM_CD (
    comm_cd, comm_nm, comm_desc, create_user_id, create_dt
) values (
             #{commCd}
         , #{commNm}
         , #{commDesc}
         , #{createUserId}
         , #{createDt}
         )

mapper.update:
UPDATE TB_COMM_CD
SET comm_nm  = #{commNm}
  , comm_desc  = #{commDesc}
  , UPDATE_USER_ID  = #{updateUserId}
  , UPDATE_DT  = #{updateDt}
WHERE comm_cd = #{commCd}

mapper.select:
select comm_dtl_cd, comm_cd, comm_dtl_nm, comm_dtl_desc, use_yn, create_user_id, create_dt
from TB_COMM_DTL_CD


        AND comm_dtl_cd = #{commDtlCd}


        AND comm_cd = #{commCd}


        AND comm_dtl_nm = #{commDtlNm}


        AND comm_dtl_desc = #{commDtlDesc}


        AND use_yn = #{useYn}


        AND create_user_id = #{createUserId}


        AND create_dt = #{createDt}

select comm_dtl_cd, comm_cd, comm_dtl_nm, comm_dtl_desc, use_yn, create_user_id, create_dt, update_user_id, update_dt
from TB_COMM_DTL_CD
where comm_dtl_cd = #{commDtlCd}
  and COMM_CD = #{commCd}

mapper.insert:
INSERT INTO TB_COMM_DTL_CD (
    comm_dtl_cd, comm_cd, comm_dtl_nm, comm_dtl_desc, use_yn, create_user_id, create_dt
) values (
             #{commDtlCd}
         , #{commCd}
         , #{commDtlNm}
         , #{commDtlDesc}
         , #{useYn}
         , #{createUserId}
         , #{createDt}
         )

mapper.update:
UPDATE TB_COMM_DTL_CD
SET comm_dtl_nm  = #{commDtlNm}
  , comm_dtl_desc  = #{commDtlDesc}
  , use_yn  = #{useYn}
  , UPDATE_USER_ID  = #{updateUserId}
  , UPDATE_DT  = #{updateDt}
WHERE comm_dtl_cd = #{commDtlCd}
  and comm_cd = #{commCd}

mapper.delete:
DELETE
FROM TB_COMM_DTL_CD
WHERE comm_dtl_cd = #{commDtlCd}
  and comm_cd = #{commCd}

mapper.select:
/* CommCdMapper.selectCommCdListAse */ SELECT COMM_CD , COMM_NM , COMM_DESC , CREATE_USER_ID , CREATE_DT , UPDATE_USER_ID , UPDATE_DT FROM TB_COMM_CD
AND COMM_CD = #{commCd}
AND COMM_NM = #{commNm}

mapper.insert:
/* CommCdMapper.insertCommCdAse */

INTO TB_COMM_CD (
             COMM_CD
           , COMM_NM
           , COMM_DESC
           , CREATE_USER_ID
           , CREATE_DT
) VALUES (
             #{item.commCd}
           , #{item.commNm}
           , #{item.commDesc}
           , #{item.createUserId}
           , SYSDATE
)

mapper.update:
/* CommCdMapper.updateCommCdAse */

UPDATE TB_COMM_CD
SET COMM_NM = #{item.commNm}
  , COMM_DESC = #{item.commDesc}
  , UPDATE_USER_ID = #{item.updateUserId}
  , UPDATE_DT = SYSDATE
WHERE COMM_CD = #{item.commCd}

mapper.select:
/* CommCdMapper.selectCommDtlCdListAse */ SELECT COMM_DTL_CD , COMM_CD , COMM_DTL_NM , COMM_DTL_DESC , USE_YN , CREATE_USER_ID , CREATE_DT , UPDATE_USER_ID , UPDATE_DT FROM TB_COMM_DTL_CD
AND COMM_CD = #{commCd}
AND COMM_DTL_CD = #{commDtlCd}

mapper.insert:
/* CommCdMapper.insertCommDtlCdAse */
INSERT INTO TB_COMM_DTL_CD (
             COMM_DTL_CD
           , COMM_CD
           , COMM_DTL_NM
           , COMM_DTL_DESC
           , USE_YN
           , CREATE_USER_ID
           , CREATE_DT
) VALUES (
             #{commDtlCd}
         , #{commCd}
         , #{commDtlNm}
         , #{commDtlDesc}
         , #{useYn}
         , #{createUserId}
         , SYSDATE
         )

mapper.update:
/* CommCdMapper.updateCommDtlCdAse */
UPDATE TB_COMM_DTL_CD
SET COMM_DTL_NM = #{commDtlNm}
  , COMM_DTL_DESC = #{commDtlDesc}
  , USE_YN = #{useYn}
  , UPDATE_USER_ID = #{updateUserId}
  , UPDATE_DT = SYSDATE
WHERE COMM_CD = #{commCd}
  AND COMM_DTL_CD = #{commDtlCd}

/* CommCdMapper.updateUseYnDtlCdAse */
UPDATE TB_COMM_DTL_CD
SET USE_YN = #{useYn}
  , UPDATE_USER_ID = #{updateUserId}
  , UPDATE_DT = SYSDATE
WHERE COMM_CD = #{commCd}
  AND COMM_DTL_CD = #{commDtlCd}
