mapper.insert:
insert into TB_COMM_MEMBER_TEMP (
      MEM_UID
    , MEM_NAME
    , MEM_PHONE
    , MEM_REG_NUM
    , MEM_CHK
    , MEM_RDO
    , MEM_DESC
    , CREATE_DT
) values (
       SYS_GUID()
     , #{memName}
     , #{memPhone}
     , #{memRegNum}
     , #{memChk}
     , #{memRdo}
     , #{memDesc}
     , SYSDATE
)
