mapper.select:
/* YjsCommCdMapper.yjsCommCdForm */
SELECT COMM_CD
     , COMM_NM
     , COMM_DESC
FROM TB_COMM_CD
WHERE COMM_CD = #{commCd}

mapper.insert:
/* YjsCommCdMapper.insertCommCd */
INSERT INTO TB_COMM_CD(
                        COMM_CD
                      , COMM_NM
                      , COMM_DESC
) VALUES (
             #{commCd}
         , #{commNm}
         , #{commDesc}
         )

mapper.update:
/* YjsCommCdMapper.updateCommCd */
UPDATE TB_COMM_CD
SET COMM_NM  = #{commNm}
  , COMM_DESC  = #{commDesc}
WHERE COMM_CD = #{commCd}

mapper.delete:
/* YjsCommCdMapper.deleteCommCd */
DELETE
FROM TB_COMM_CD
WHERE COMM_CD = #{commCd}

mapper.select:
/* YjsCommCdMapper.yjsCommDtForm */
SELECT COMM_CD
     , COMM_DT_CD
     , COMM_DT_NM
     , COMM_DT_DESC
     , SORT_ORDER
     , COMM_DT_GRP
FROM TB_COMM_DT
WHERE COMM_CD = #{commCd}
  AND COMM_DT_CD = #{commDtCd}

mapper.insert:
/* YjsCommCdMapper.insertCommDt */
INSERT INTO TB_COMM_DT(
                      COMM_CD
                    , COMM_DT_CD
                    , COMM_DT_NM
                    , COMM_DT_DESC
                    , SORT_ORDER
                    , COMM_DT_GRP
) VALUES (
          #{commCd}
        , #{commDtCd}
        , #{commDtNm}
        , #{commDtDesc}
        , #{sortOrder}
        , #{commDtGrp}
                        )

mapper.update:
/* YjsCommCdMapper.updateCommDt */
UPDATE TB_COMM_DT
SET COMM_CD  = #{commCd}
  , COMM_DT_CD  = #{commDtCd}
  , COMM_DT_NM  = #{commDtNm}
  , COMM_DT_DESC  = #{commDtDesc}
  , SORT_ORDER  = #{sortOrder}
  , COMM_DT_GRP  = #{commDtGrp}
WHERE COMM_CD = #{commCd}
  AND COMM_DT_CD = #{commDtCd}

mapper.delete:
/* YjsCommCdMapper.deleteCommDt */
DELETE
FROM TB_COMM_DT
WHERE COMM_CD = #{commCd}
  AND COMM_DT_CD = #{commDtCd}
